struct AnimatedSVGView: View {
    @State private var drawAmount: CGFloat = 0
    
    var body: some View {
        ZStack {
            MyIcon().path(in: CGRect(x: 0, y: 0, width: 200, height: 200))
                .trim(from: 0, to: drawAmount)
                .stroke(Color("Red"), lineWidth: 2)
                .animation(.easeInOut(duration: 1.5), value: drawAmount)
        }
        .onAppear {
            drawAmount = 1
        }
    }
}

struct SplashView: View {
    @State private var circleIsHiden = true
    @State private var isMainViewPresented = false
    
    var body: some View {
        ZStack {
            Color("White")
                .edgesIgnoringSafeArea(.all)
            VStack {
                ZStack {
                    Circle()
                        .foregroundColor(Color("Black").opacity(0.2))
                        .frame(width: 250, height: 250)
                        .opacity(circleIsHiden ? 0 : 1)
                    AnimatedSVGView()
                        .frame(width: 200, height: 200)
                }
                Text("My Workflows Manager")
                    .font(.title.bold())
                    .opacity(circleIsHiden ? 0 : 1)
            }
        }
        .fullScreenCover(isPresented: $isMainViewPresented) {
            MainView(viewModel: MainViewModel())
        }
        .onAppear() {
            logger.info("app started")
            withAnimation(.easeInOut(duration: 1.5)) {
                circleIsHiden.toggle()
            }
            DispatchQueue.main.asyncAfter(deadline: .now() + 2) {
                logger.info("splash screen completed")
                isMainViewPresented.toggle()
            }
            Task {
                let userConfig = try? await UserConfigurationManager.shared.fetchSettings()
                guard userConfig == nil else { return }
#if DEBUG
                print("Update local config to CloudKit")
#endif
                try? await UserConfigurationManager.shared.updateSettings(UserDefaultsHelper.shared.userConfig)
            }
        }
    }
}

